def length_of_tableau(tableau)
	length_of_array = tableau.length
	puts "La taille de l'array est de #{length_of_array}."
end

def contain_num(tableau)
	result = 0
	tableau.each do |item|
		if item.include?("0") || item.include?("1") || item.include?("2") || item.include?("3") || item.include?("4") || item.include?("5") || item.include?("6") || item.include?("7") || item.include?("8") || item.include?("9")
			result += 1
		end
	end
	puts "Le nombre de handle qui contiennent un numéro est de #{result}."
end

def contain_aude(tableau)
	result = 0
	tableau.each do |item|
		if item.include?("Aude") || item.include?("AUDE") || item.downcase.include?("aude")
			result += 1
		end
	end
	puts "Le nombre de handle qui contennent les lettres \"Aude\" est de #{result}."
end

def start_with_upper(tableau)
	result = -3

	tableau.each do |item|
		i = 1
		while item.slice(i) == "_" || item.slice(i) == "-"
			i += 1
		end
		if item.slice(i) == item.slice(i).upcase
			result += 1
		end
	end
	puts "Le nombre de handle qui commencent par une majuscule est de #{result}."
end

def contain_maj(tableau)
  d = tableau.grep(/[A-Z]/).count
    puts "Le nombre de handle qui contiennent des majuscules est de #{d}."
end

def contain_underscore(tableau)
	result = 0
	tableau.each do |item|
		item.each_char do |c|
			if c == '_'
				result += 1
			end 
		end
	end
	puts "Le nombre total d'undescore est de #{result}."
end

def sort(tableau)

puts tableau.sort_by{ |x| x.downcase }

end

